import React from 'react';
import Item from './Item';

class App extends React.Component {
    constructor() {
        super();
        this.state = {
            data: [
                { name: 'Milk' },
                { name: 'Bread' },
                { name: 'Butter' }
            ]
        }
    }

    add() {
        var items = this.state.data;
        items.push('Apple');
        this.setState({
            data: items
        });
    }

    render() {
        return (
            <div>
                <ul>
                    {this.state.data.map(function(v) {
                        return <Item name={v.name} />
                    })}
                </ul>
                <button onClick={this.add}>Button</button>
            </div>
        )
    }
}

export default App;
